mapper.select:
SELECT COUNT(*)
FROM MEMBER_INFO
WHERE MEMBER_CODE = #{member_code} AND PW LIKE #{pw}

SELECT ID, PW, NAME, TEL, NICKNAME
FROM MEMBER_INFO
WHERE MEMBER_CODE = #{member_code}

SELECT PW
FROM MEMBER_INFO
WHERE MEMBER_CODE = #{member_code}

mapper.update:
UPDATE MEMBER_INFO
SET PW=#{pw}, TEL=#{tel}, NICKNAME=NVL(#{nickname}, 'user#'||MEMBER_NICKNAME_SEQ.NEXTVAL)
WHERE MEMBER_CODE = #{member_code}
